import React, { useState } from 'react'
import { useNavigate } from "react-router-dom"
import './Home.css'; 

const Home = () => {
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const navigate = useNavigate();

  const handleProfileClick = () => {
    setShowProfileDropdown(!showProfileDropdown);
  }

  const handleLevelSelect = (difficulty) => {
    navigate('/Quiz', { state: { difficulty } });
  }

  return (
    <div className="home-page">
      <header>
        <div className="profile-container">
          <button className="profile-button" onClick={handleProfileClick}>
            <img src="path/to/profile-image.jpg" alt="Profile" />
          </button>
          {showProfileDropdown && (
            <div className="profile-dropdown">
              {/* User details go here */}
              <p>User Name</p>
              <p>Email@example.com</p>
              
            </div>
          )}
        </div>
      </header>
      <main>
        <section className="quote-section">
          <h1>Welcome to the Quiz Application!</h1>
          <p>
            "The only limit to our realization of tomorrow will be our doubts of today."
          </p>
        </section>
        <section className="instructions-section">
          <h2>Instructions:</h2>
          <p>Read each question carefully and choose the correct answer.</p>
          <p>There is a time limit for each question, so answer quickly!</p>
        </section>
        <section className="start-quiz-section">
          <button className="select-level-button" onClick={() => handleLevelSelect('easy')}>Select Level: Easy</button>
          <button className="select-level-button" onClick={() => handleLevelSelect('medium')}>Select Level: Medium</button>
          <button className="select-level-button" onClick={() => handleLevelSelect('hard')}>Select Level: Hard</button>
        </section>
      </main>
    </div>
  );
}

export default Home;
